const TABLET_WIDTH=768,MOBILE_MAX_WIDTH=767,menuButton=document.querySelector(".main-nav__toggle"),menuButtonText=menuButton.querySelector("span"),mainNavWrapper=document.querySelector(".main-nav__wrapper"),buttonClickHandler=()=>{mainNavWrapper.classList.toggle("visually-hidden"),menuButton.classList.contains("main-nav__toggle--open")?(menuButton.classList.remove("main-nav__toggle--open"),menuButton.classList.add("main-nav__toggle--close"),menuButtonText.textContent="Закрыть меню"):(menuButton.classList.remove("main-nav__toggle--close"),menuButton.classList.add("main-nav__toggle--open"),menuButtonText.textContent="Открыть меню")};window.innerWidth<768&&(mainNavWrapper.classList.add("visually-hidden"),menuButton.classList.remove("visually-hidden"),menuButton.addEventListener("click",buttonClickHandler));const popup=document.querySelector(".add-cart"),orderButton=document.querySelector(".featured-product__order"),orderHandler=e=>{if(window.innerWidth>767)return;e.preventDefault(),popup.classList.remove("visually-hidden");const t=document.querySelector("body");t.classList.add("scroll-hidden");const n=popup.querySelector(".add-cart__button"),o=e=>{e.preventDefault(),t.classList.remove("scroll-hidden"),popup.classList.add("visually-hidden"),n.removeEventListener("click",o)};n.addEventListener("click",o)};orderButton.addEventListener("click",orderHandler);const sizeButtons=popup.querySelectorAll(".add-cart__size");for(let e of sizeButtons)e.addEventListener("click",()=>{popup.querySelector(".add-cart__size--active").classList.remove("add-cart__size--active"),e.classList.add("add-cart__size--active")});const addButtons=document.querySelectorAll(".product-card__add");for(let e of addButtons)e.addEventListener("click",orderHandler);